/**
 * Authorise function, used as Slim Route Middlewear
 */
function authorize($papel = "user") {
    return function () use ( $papel ) {
        // Get the Slim framework object
        $app = \Slim\Slim::getInstance();
        // First, check to see if the user is logged in at all
        if(!empty($_SESSION['usuario'])) {
            // Next, validate the role to make sure they can access the route
            // We will assume admin role can access everything
            if($_SESSION['usuario']['papel'] == $papel ||
                $_SESSION['usuario']['papel'] == 'admin') {
                //User is logged in and has the correct permissions... Nice!
                return true;
            }
            else {
                // If a user is logged in, but doesn't have permissions, return 403
                $response = array(
                    'type' => 'sem_permissao',
                    'message' => 'Você não tem permissão para acessar este recurso.'
                );
                $app->halt(403, json_encode($response));
                //$app->halt(403, 'You shall not pass!');
            }
        }
        else {
            // If a user is not logged in at all, return a 401
            $response = array(
                'type' => 'nao_logado',
                'message' => 'Você deve estar logado para acessar este recurso.'
            );
            $app->halt(401, json_encode($response));
            //$app->halt(401, 'You shall not pass!');
        }
    };
}

