function arrayToUtf8(&$array) {
    array_walk($array, function(&$valor, $chave) {
        if (is_array($valor)) {
            arrayToUtf8($valor);
        } else {
            $valor = utf8_encode($valor);
        }
    });
}

function logoff() {
    $app = \Slim\Slim::getInstance();
    session_destroy();
    $app->response()->header('Content-Type', 'application/json');
    echo '{"success":{"text":""}}';
}

function cadastrarLog($nom_acao=null, $nom_tabela=null, $nom_campo=null,
                      $old_txt_value=null, $new_txt_value=null, $old_blob_value=null, $new_blob_value=null) {

    $log_ORM = ORM::for_table('log')->create();
    $log_ORM->set('nom_usuario', $_SESSION['usuario']['email']);
    $log_ORM->set('num_ip', $_SERVER['REMOTE_ADDR']);
    $log_ORM->set('nom_acao', $nom_acao);
    $log_ORM->set('nom_tabela', $nom_tabela);
    $log_ORM->set('nom_campo', $nom_campo);
    $log_ORM->set('old_txt_value', $old_txt_value);
    $log_ORM->set('new_txt_value', $new_txt_value);
    $log_ORM->set('old_blob_value', $old_blob_value);
    $log_ORM->set('new_blob_value', $new_blob_value);
    $log_ORM->save();
}
